sys_variable_value.value:
(function execute(inputs, outputs) {

    var Response = false;

    var atualizarCreditos = new createRecordAud();

    Response = atualizarCreditos.atualizarCreditos(inputs.parameter_solicitante, inputs.parameter_solicitacao, inputs.parameter_valor, inputs.parameter_quantidade);

    outputs.parameter_response = Response;

})(inputs, outputs);

